def cosine_embedding_loss(cos_sim, target, margin, reduction: tl.constexpr):
    """Computes the cosine embedding loss."""
    loss = tl.maximum(0, target * cos_sim - margin)
    if reduction == 'mean':
        return tl.mean(loss)
    elif reduction == 'sum':
        return tl.sum(loss)
    else:
        return loss

